async ({ province }) => {
                const res = await fetch('https://api.allorigins.win/get?url=http%3A%2F%2Fnmc.cn%2Frest%2Fprovince%2F'+ province);
                const citys = await res.json();
                const q = JSON.parse(citys.contents);
                return q[0].code
            }

